import React, { useState, useEffect } from 'react'
import personService from './services/persons'
import ViewPersons from './components/ViewPersons'
import AddPersonForm from './components/AddPersonForm'
import FilterForm from './components/FilterForm'
import Notification from './components/Notification'

const useAlert = message => {
  /*   return (
      <Notification message={message} />
    ) */

  if (message === null) {
    return null
  }

  return (
    alert(message)
  )

}

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')
  const [errorMessage, setErrorMessage] = useState('virhe...')


  useEffect(() => {
    personService.getAll().then(initialPersons => {
      setPersons(initialPersons)
    })
  }, [])

  const addPerson = (event) => {
    event.preventDefault()

    //Estää saman nimen lisäämisen toiseen kertaan
    //const names = persons.find(n => n.name === newName)

    /*     console.log(persons.indexOf(newName), newName)
        console.log(persons) */
    /*     if (persons.find(newName) !== -1) {
          setMessage(`${newName} on jo luettelossa`)
          makeNotification(message)
          return null
        } */

    /*     setMessage(`${newName} on jo luettelossa`)
        makeNotification(message) */

    const newPerson = {
      name: newName,
      number: newNumber,
    }

    personService.create(newPerson).then(returnedPersons => {
      setPersons(persons.concat(returnedPersons))
      setNewName('')
      setNewNumber('')

    }).catch(error => {
      setErrorMessage('Virhe')
      setTimeout(() => {
        setMessage('')
      }, 5000)
      useAlert(errorMessage)
    })

    //const alert = `Lisättiin '${newName}'`
    setErrorMessage(`Lisättiin '${newName}'`)
    console.log(errorMessage)
    useAlert(errorMessage)

    /*     setTimeout(() => {
          setMessage('')
        }, 5000) */


    setPersons(persons.concat(newPerson))
    setNewName('')
    setNewNumber('')
  }


  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    setFilter(event.target.value)
  }

  //palaa splice metodiin??
  const handleRemove = (id) => {
    const rightPerson = persons.find(n => n.id === id)
    personService.remove(rightPerson.id).then(returnedPerson => {
      setPersons(persons.splice(returnedPerson))
      setNewName('')
      setNewNumber('')
    })

    setPersons(persons.splice(rightPerson, 1))
    setNewName('')
    setNewNumber('')
  }

  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <FilterForm
        filter={filter}
        handleFilter={handleFilter}
      />

      <h2>Lisää uusi</h2>
      <AddPersonForm
        addPerson={addPerson}
        handleNewName={handleNewName}
        handleNewNumber={handleNewNumber}
        newName={newName}
        newNumber={newNumber}
      />


      <h2>Numerot</h2>
      <ViewPersons
        persons={persons}
        filter={filter}
        handleRemove={handleRemove}
      />
    </div >
  )

}

export default App
