import React, { useState } from 'react'
import RenderPerson from './components/RenderPerson'


/* const AnotherPerson = (props) => {

  const newPerson = {
    number: props.newNumber,
    name: props.newName,
    id: props.persons.length + 1
  }

  return newPerson
}
 */


const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [showPersons, setShowPersons] = useState('')

  const addPerson = (event) => {
    event.preventDefault()


    const names = persons.map(i => i.name)
    if (names.includes(newName)) {
      alert(`${newName} on jo luettelossa`)
      return
    }

    const newPerson = {
      number: newNumber,
      name: newName,
      id: persons.length + 1,
    }

    setPersons(persons.concat(newPerson))
    setNewName('')
    setNewNumber('')
  }

  const handleNewName = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    console.log(event.target.value)
    namesToShow(event.target.value)
  }

  const namesToShow = (event) => persons.map((person) => 
    <RenderPerson
    key={person.id}
    person={person.includes(event.value)} 
    />
  )

/*   const rows = () => namesToShow.map((person) =>
    <RenderPerson
      key={person.id}
      person={person}
    />
  ) */

  return (
    <div>
      <h2>Puhelinluettelo</h2>

      <div>
          rajaa näytettäviä: <input
            value={showPersons}
            onChange={handleFilter} />
        </div>

      <form onSubmit={addPerson}>

        <div>
          nimi: <input
            value={newName}
            onChange={handleNewName} />
        </div>

        <div>
          numero: <input
            value={newNumber}
            onChange={handleNewNumber} />
        </div>

        <div>
          <button type="submit">lisää </button>
        </div>

      </form>

      <h2>Numerot</h2>
      <ul>
        {namesToShow()}
      </ul>
    </div>
  )

}

export default App
