import React, { useState, useEffect } from 'react'
import personService from './services/persons'
import ViewPersons from './components/ViewPersons'
import AddPersonForm from './components/AddPersonForm'
import FilterForm from './components/FilterForm'


const Notification = ({ message }) => {
  if (message === null) {
    return null
  }

  return (
    <div className="error">
      {message}
    </div>
  )
}

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')
  const [message, setMessage] = useState('')


  useEffect(() => {
    personService.getAll().then(initialPersons => {
      setPersons(initialPersons)
    })
  }, [])

  const addPerson = (event) => {
    event.preventDefault()

    //Est채채 saman nimen lis채채misen toiseen kertaan
    const names = persons.map(i => i.name)
    if (names.includes(newName)) {
      alert(`${newName} on jo luettelossa`)
      return (
        <Notification message={`${newName} on jo luettelossa`} />
      )
    }

    const newPerson = {
      name: newName,
      number: newNumber,
      //id: persons.length + 1,
    }

    personService.create(newPerson).then(returnedPersons => {
      setPersons(persons.concat(returnedPersons))
      setNewName('')
      setNewNumber('')
    }).catch(message => {
      setMessage(
        `Henkil철 '${newName}' lis채tty palvelielle`
      )
      setTimeout(() => {
        setMessage(null)
      }, 5000)


      setPersons(persons.concat(newPerson))
      setNewName('')
      setNewNumber('')
    })
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    setFilter(event.target.value)
  }

  const handleRemove = (id) => {
    const rightPerson = persons.find(n => n.id === id)
    personService.remove(rightPerson.id).then(returnedPerson => {
      setPersons(persons.splice(returnedPerson))
      setNewName('')
      setNewNumber('')
    })

    setPersons(persons.splice(rightPerson, 1))
    setNewName('')
    setNewNumber('')
  }

  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <FilterForm
        filter={filter}
        handleFilter={handleFilter}
      />

      <h2>Lis채채 uusi</h2>
      <AddPersonForm
        addPerson={addPerson}
        handleNewName={handleNewName}
        handleNewNumber={handleNewNumber}
        newName={newName}
        newNumber={newNumber}
      />

      <h2>Numerot</h2>
      <ViewPersons
        persons={persons}
        filter={filter}
        handleRemove={handleRemove}
      />
    </div >
  )

}

export default App
