import React from 'react'
import RenderPerson from './RenderPerson'


const Person = ({ person, handleRemove }) => {
    return (
            <li>
                {person.name} {person.number} <button onClick={handleRemove}>poista</button>
            </li>

    )
}


const ViewPersons = ({ persons, filter, handleRemove }) => {
    console.log(persons, filter)
    const filteredNames = persons.filter(person => person.name.includes(filter))
    console.log(filteredNames, filter)

    //case insensitiivisen filtterin toteuttaminen=

    return (
        filteredNames.map(person =>
            <Person
                key={person.id}
                person={person}
                handleRemove={() => handleRemove(person.id)}
            />
        )
    )
}

export default ViewPersons
