import React, { useState, useEffect } from 'react'
import axios from 'axios'
import personService from './services/persons'
import ViewPersons from './components/ViewPersons'
import RenderPerson from './components/RenderPerson'
import AddPersonForm from './components/AddPersonForm'
import FilterForm from './components/FilterForm'



const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  useEffect(() => {
    personService.getAll().then(initialPersons => {
      setPersons(initialPersons)
    })
  }, [])

  const addPerson = (event) => {
    event.preventDefault()

    //Onko sama nimi jo olemassa
    const names = persons.map(i => i.name)
    if (names.includes(newName)) {
      alert(`${newName} on jo luettelossa`)
      return
    }

    const newPerson = {
      name: newName,
      number: newNumber,
      id: persons.length + 1,
    }

    personService.create(newPerson).then(returnedPersons => {
      setPersons(persons.concat(returnedPersons))
      setNewName('')
      setNewNumber('')
    })

    setPersons(persons.concat(newPerson))
    setNewName('')
    setNewNumber('')
  }


  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    setFilter(event.target.value)
  }

  const handleRemove = id => {
    const rightPerson = persons.find(n => n.id === id)
    personService.delete(rightPerson).then(returnedPersons => {
      setPersons(persons.concat(returnedPersons))
      setNewName('')
      setNewNumber('')
    })

    setPersons(persons.splice(rightPerson, 1))
    setNewName('')
    setNewNumber('')
  }




return (
  <div>
    <h1>Puhelinluettelo</h1>
    <FilterForm
      filter={filter}
      handleFilter={handleFilter}
    />

    <h2>Lisää uusi</h2>
    <AddPersonForm
      addPerson={addPerson}
      handleNewName={handleNewName}
      handleNewNumber={handleNewNumber}
      newName={newName}
      newNumber={newNumber}
    />

    <h2>Numerot</h2>
    <ViewPersons
      persons={persons}
      filter={filter}
    />
  </div >
)

}

export default App
