import React, { useState, useEffect } from 'react'
import axios from 'axios'
import personService from './services/persons'
import ViewPersons from './components/ViewPersons'
import AddPersonForm from './components/AddPersonForm'
import FilterForm from './components/FilterForm'



const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  useEffect(() => {
    personService.getAll().then(initialPersons => {
      setPersons(initialPersons)
    })
  }, [])

  const addPerson = (event) => {
    event.preventDefault()

    const names = persons.map(i => i.name)
    if (names.includes(newName)) {
      alert(`${newName} on jo luettelossa`)
      return
    }

    const newPerson = nextPerson(newName, newNumber, persons.length + 1)

    personService.create(newPerson).then(returnedPersons => {
      setPersons(persons.concat(returnedPersons))
      setNewName('')
      setNewNumber('')
    })

    setPersons(persons.concat(newPerson))
    setNewName('')
    setNewNumber('')
  }

  const nextPerson = ({newName, newNumber, id}) => {
    const newPerson = {
      name: newName,
      number: newNumber,
      id: id
    }
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    setFilter(event.target.value)
  }

  /*   const handleRemove= (event) => {
      console.log(event.target.value)
      setPersons(removePerson(persons, event.target.id))
    }

    const removePerson = ({persons}, {id}) => {
      persons.splice(id, 1)
    } */


  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <FilterForm
        filter={filter}
        handleFilter={handleFilter}
      />

      <h2>Lisää uusi</h2>
      <AddPersonForm
        addPerson={addPerson}
        handleNewName={handleNewName}
        handleNewNumber={handleNewNumber}
        newName={newName}
        newNumber={newNumber}
      />

      <h2>Numerot</h2>
      <ViewPersons
        persons={persons}
        value={filter}
      />
    </div >
  )

}

export default App
