import axios from 'axios'
//import react from 'react'

const baseUrl = '/api/persons'

const getAll = () => {
  const request = axios.get(baseUrl)
  return request.then(response => response.data)
}

const create = newObject => {

  // Estää toisen samannimisen kaverin lisäämisen
  getAll().then(persons => {
    console.log("data:", persons)
    if (persons.map(person => person.name).includes(newObject.name)) {
      console.log("sama nimi")

      return reject(new Error(`${newObject.name} on jo luettelossa`))
    }
  })

  console.log("testatatan", newObject)
  const request = axios.post(baseUrl, newObject)
  console.log("testataastan", request)
  console.log(request.then(response => response.data))
  return request.then(response => response.data)
};

const update = (id, newObject) => {
  const request = axios.put(`${baseUrl}/${id}`, newObject)
  return request.then(response => response.data)
}

const remove = id => {
  const request = axios.delete(`${baseUrl}/${id}`)
  return request.then(response => response.data)
}

export default { getAll, create, update, remove }
