import React, { useState, useEffect } from 'react'
import axios from 'axios'
import RenderPerson from './components/RenderPerson'



const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')


  const hook = () => {
    console.log('effect')
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
      })
  }

  useEffect(hook, [])
  console.log('render', persons.length, 'notes')

  const addPerson = (event) => {
    event.preventDefault()

    const names = persons.map(i => i.name)
    if (names.includes(newName)) {
      alert(`${newName} on jo luettelossa`)
      return
    }

    const newPerson = {
      number: newNumber,
      name: newName,
      id: persons.length + 1,
    }

    setPersons(persons.concat(newPerson))
    setNewName('')
    setNewNumber('')
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    console.log(event.target.value)
    setFilter(event.target.value)
  }

  //case insensitiivinen filtteri?
  const filteredNames = persons.filter(person => person.name.includes(filter))
  console.log(filteredNames)

  const rows = () => filteredNames.map((person) =>
    <RenderPerson
      key={person.id}
      person={person}
    />
  )

  return (
    <div>
      <h1>Puhelinluettelo</h1>

      <div>
        rajaa näytettäviä: <input
          value={filter}
          onChange={handleFilter} />
      </div>

      <h2>Lisää uusi</h2>

      <form onSubmit={addPerson}>
        <div>
          nimi: <input
            value={newName}
            onChange={handleNewName} />
        </div>
        <div>
          numero: <input
            value={newNumber}
            onChange={handleNewNumber} />
        </div>
        <div>
          <button type="submit">lisää </button>
        </div>
      </form>

      <h2>Numerot</h2>
      <ul>
        {rows()}
      </ul>
    </div >
  )

}


