import React from 'react'
import RenderPerson from './RenderPerson'


const ViewPersons = (props) => {
    console.log(props.persons)
    const filteredNames = props.persons.filter(person => person.name.includes(props.filter))
    console.log(filteredNames)

    /*    return (
           <div>
               {filteredNames.map(person =>

                   <li>
                       {person.name} {person.number}
                       <button onClick={props.handleRemove(person.id)}>poista</button>
                   </li>

               )}
           </div> */

    return (
        filteredNames.map(person => {
            <RenderPerson
                key={person.id}
                person={person}
                handleRemove={() => props.handleRemove(person.id)}
            />
        }
        )
    )

}

export default ViewPersons
